mapper.insert:
INSERT INTO PC_MEMBER (
	 MNUM
	,MNAME
	,MID
	,MPW
	,MHP
	,MEMAIL
	,MPET
	,MINFO
	,DELYN
	,IDATE
	,UDATE
)
VALUES (
	 #{ mnum }
	,#{ mname, jdbcType=VARCHAR }
	,#{ mid, jdbcType=VARCHAR }
	,#{ mpw, jdbcType=VARCHAR }
	,#{ mhp, jdbcType=VARCHAR }
	,#{ memail, jdbcType=VARCHAR }
	,#{ mpet, jdbcType=VARCHAR }
	,#{ minfo, jdbcType=VARCHAR }
	,'Y'
	,SYSDATE
	,SYSDATE
)

mapper.update:
UPDATE   PC_MEMBER
SET      MHP          = #{ mhp }
        ,MEMAIL       = #{ memail }
        ,MPET         = #{ mpet }
        ,MINFO        = #{ minfo }
        ,UDATE        = SYSDATE
WHERE    DELYN        = 'Y'
AND      MNUM         = #{ mnum }
